function mapax=PSShorePlot(latmin,latmax,lonmin,lonmax)%function mapax=PSShorePlot(latmin,latmax,lonmin,lonmax)%Plot up the shoreline using the 30-m data from David Finlayson.%The old version, PSShorePlot300, used the 300-m data from Miles Logsdon.%Please adjust the path within the script in order to locate PSTM_Shoreline.mat%if nargin == 0    latmin=47;    latmax=48.5;    lonmin=122.0000;    lonmax=123.4000;endPSBathy30Path='/Users/kim/RESEARCH/MatlabScripts/mulitplot/';%PSBathy30Path='C:\MATLAB6p5\Research'shorefile=fullfile(PSBathy30Path,'PSTM_shoreline.mat');eval(['shoreline=load(''' shorefile ''');'])plot(-shoreline.lon_sh,shoreline.lat_sh)ff=.001;axis([lonmin-ff lonmax+ff latmin-ff latmax+ff ])rat=(latmax-latmin)/(lonmax-lonmin);set(gca,'XDir','reverse')ff=.001;axis([lonmin-ff lonmax+ff latmin-ff latmax+ff ])%ylabel('Latitude / degrees')xlabel('West Longitude / degrees') ylabel('North Latitude / degrees') dar=get(gca,'dataaspectratio');latmid=1/2*(latmin+latmax);dar(1)=dar(1).*cos(latmid/180*pi);set(gca,'plotboxaspectratio',dar)set(gca,'box','on')mapax=gca;